import pytest
from src.MaquinaTuring import MaquinaTuring


@pytest.mark.parametrize(
    "input, expected",
    [
        ("01011011101101001110110111011010011101110110111010001101101101110", True),
        ("01011011101101001110110111011010011101011010111000110110110", True),
        (
            "",
            False,
        ),
        (
            "",
            True,
        ),
        (
            "01011011101111010011101101110110100111011111011101111101001110111011110111110110011110110111101101100111101111101111011111011001111011110101111010010111110111110111110100111110111110111110111110100111110101101011100101011010111000110110110110111011101110",
            False,
        ),
        (
            "",
            True,
        ),
    ],
)
def test_validar_cadena(input, expected):
    maquinaTuring = MaquinaTuring(input)
    assert maquinaTuring.evaluar_cadena() == expected
